package pass.users.api.spec.handler.user.integration

import kotlinx.coroutines.runBlocking
import org.junit.Test
import org.junit.runner.RunWith
import org.springframework.beans.factory.annotation.Autowired
import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc
import org.springframework.boot.test.context.SpringBootTest
import org.springframework.http.MediaType
import org.springframework.mock.web.MockMultipartFile
import org.springframework.test.context.ActiveProfiles
import org.springframework.test.context.junit4.SpringRunner
import org.springframework.test.web.servlet.MockMvc
import org.springframework.test.web.servlet.request.MockMvcRequestBuilders
import org.springframework.test.web.servlet.result.MockMvcResultMatchers
import pass.users.api.PassUsersAPI
import java.util.*

@RunWith(SpringRunner::class)
@ActiveProfiles("test")
@SpringBootTest(classes = [PassUsersAPI::class])
@AutoConfigureMockMvc
class UserImageHandlerTest {

    @Autowired
    private lateinit var mockMvc: MockMvc

    fun getImageBytes(): ByteArray {
        val base64Image =
            ""

        return Base64.getDecoder().decode(base64Image.toByteArray(charset("UTF-8")))
    }


    @Test
    fun `test create`(): Unit = runBlocking {
        val file = MockMultipartFile(
            "blank.jpg",
            "blank.jpg",
            "image/jpg",
            getImageBytes()
        )

        mockMvc.perform(
            MockMvcRequestBuilders.multipart("/user-photo/")
                .file(file)
                .accept(MediaType.APPLICATION_JSON)
        ).andExpect(
            MockMvcResultMatchers.status().isOk
        )
        Unit
    }
}
